mapper.delete:
update PRM_PLAN 
set dr = 1
where ID = #{id}

mapper.update:
begin
update PRM_PLAN
PK_DEPT = #{item.pk_dept,jdbcType=VARCHAR}, PK_DEPT_NAME = #{item.pk_dept_name,jdbcType=VARCHAR}, CODE = #{item.code,jdbcType=VARCHAR}, NAME = #{item.name,jdbcType=VARCHAR}, PK_PLAN_VERSION = #{item.pk_plan_version,jdbcType=VARCHAR}, PK_PROCESS = #{item.pk_process,jdbcType=VARCHAR}, PK_PROCESS_NAME = #{item.pk_process_name,jdbcType=VARCHAR}, ENABLESTATE = #{item.enablestate,jdbcType=INTEGER}, DEFPLAN = #{item.defplan,jdbcType=INTEGER}, TENANTID = #{item.tenantid,jdbcType=VARCHAR}, INVALIDATE = #{item.invalidate,jdbcType=TIMESTAMP}, SYSID = #{item.sysid,jdbcType=VARCHAR}, ORGID = #{item.orgid,jdbcType=VARCHAR}, ORGID_NAME = #{item.orgid_name,jdbcType=VARCHAR}, TS = #{item.ts,jdbcType=TIMESTAMP}, DR = #{item.dr,jdbcType=INTEGER}, CREATOR = #{item.creator,jdbcType=VARCHAR}, CREATIONTIME = #{item.creationtime,jdbcType=TIMESTAMP}, MODIFIER = #{item.modifier,jdbcType=VARCHAR}, MODIFIEDTIME = #{item.modifiedtime,jdbcType=TIMESTAMP}, CREATOR_NAME = #{item.creator_name,jdbcType=VARCHAR}, MODIFIER_NAME = #{item.modifier_name,jdbcType=VARCHAR}, PK_POST = #{item.pk_post,jdbcType=VARCHAR}, PK_POST_NAME = #{item.pk_post_name,jdbcType=VARCHAR}, CYCLE = #{item.cycle,jdbcType=DECIMAL}, ROUTEFLAG = #{item.routeflag,jdbcType=VARCHAR}, IVERSION = #{item.iversion,jdbcType=VARCHAR}, VNOTE = #{item.vnote,jdbcType=VARCHAR}
where ID = #{item.id}
;end;

mapper.select:
select
from PRM_PLAN where dr = 0 and ID in
#{item}

select
from PRM_PLAN where CODE in
#{item}
