mapper.delete:
update PRM_PLAN_B 
set dr = 1
where ID = #{id}

mapper.update:
begin
update PRM_PLAN_B
PK_ER_PROJECT = #{item.pk_er_project,jdbcType=VARCHAR}, ER_PROJECT_CODE = #{item.er_project_code,jdbcType=VARCHAR}, ER_PROJECT_NAME = #{item.er_project_name,jdbcType=VARCHAR}, PK_PLAN = #{item.pk_plan,jdbcType=VARCHAR}, PLAN_ORDER = #{item.plan_order,jdbcType=INTEGER}, TENANTID = #{item.tenantid,jdbcType=VARCHAR}, SYSID = #{item.sysid,jdbcType=VARCHAR}, ORGID = #{item.orgid,jdbcType=VARCHAR}, ORGID_NAME = #{item.orgid_name,jdbcType=VARCHAR}, TS = #{item.ts,jdbcType=TIMESTAMP}, MODIFIER = #{item.modifier,jdbcType=VARCHAR}, MODIFIEDTIME = #{item.modifiedtime,jdbcType=TIMESTAMP}, MODIFIER_NAME = #{item.modifier_name,jdbcType=VARCHAR}
where ID = #{item.id}
;end;

mapper.select:
select
from PRM_PLAN_B where PK_PLAN in
#{item}
