mapper.delete:
update  PRM_TASK
set DR = 1
where ID = #{id}

mapper.update:
begin
update PRM_TASK
TASK_NO = #{item.task_no,jdbcType=VARCHAR}, POSTID = #{item.postid,jdbcType=VARCHAR}, POSTID_NAME = #{item.postid_name,jdbcType=VARCHAR}, PLANID = #{item.planid,jdbcType=VARCHAR}, PLANID_NAME = #{item.planid_name,jdbcType=VARCHAR}, BILLSTATUS = #{item.billstatus,jdbcType=INTEGER}, TENANTID = #{item.tenantid,jdbcType=VARCHAR}, SYSID = #{item.sysid,jdbcType=VARCHAR}, ORGID = #{item.orgid,jdbcType=VARCHAR}, ORGID_NAME = #{item.orgid_name,jdbcType=VARCHAR}, TS = #{item.ts,jdbcType=TIMESTAMP}, DR = #{item.dr,jdbcType=INTEGER}, CREATOR = #{item.creator,jdbcType=VARCHAR}, CREATIONTIME = #{item.creationtime,jdbcType=TIMESTAMP}, MODIFIER = #{item.modifier,jdbcType=VARCHAR}, MODIFIEDTIME = #{item.modifiedtime,jdbcType=TIMESTAMP}, CREATOR_NAME = #{item.creator_name,jdbcType=VARCHAR}, MODIFIER_NAME = #{item.modifier_name,jdbcType=VARCHAR}, ASSIGN_TIME = #{item.assign_time,jdbcType=TIMESTAMP}
where ID = #{item.id}
;end;

mapper.select:
select
from PRM_TASK where DR=0 and ID in
#{item}
