mapper.delete:
update  PRM_TASK_B
set DR = 1
where ID = #{id}

mapper.update:
begin
update PRM_TASK_B
PK_TASK = #{item.pk_task,jdbcType=VARCHAR}, PLAN_ORDER = #{item.plan_order,jdbcType=INTEGER}, PROJECTID = #{item.projectid,jdbcType=VARCHAR}, PROJECTID_CODE = #{item.projectid_code,jdbcType=VARCHAR}, PROJECTID_NAME = #{item.projectid_name,jdbcType=VARCHAR}, FACT_ORDER = #{item.fact_order,jdbcType=INTEGER}, PROJECT_STATUS = #{item.project_status,jdbcType=INTEGER}, PROJECT_CONTENT = #{item.project_content,jdbcType=VARCHAR}, JUDGE_STANDARD = #{item.judge_standard,jdbcType=VARCHAR}, POLLING_VALUE = #{item.polling_value,jdbcType=VARCHAR}, PIC_URL = #{item.pic_url,jdbcType=VARCHAR}, TENANTID = #{item.tenantid,jdbcType=VARCHAR}, SYSID = #{item.sysid,jdbcType=VARCHAR}, ORGID = #{item.orgid,jdbcType=VARCHAR}, ORGID_NAME = #{item.orgid_name,jdbcType=VARCHAR}, TS = #{item.ts,jdbcType=TIMESTAMP}, DR = #{item.dr,jdbcType=INTEGER}, CREATOR = #{item.creator,jdbcType=VARCHAR}, CREATIONTIME = #{item.creationtime,jdbcType=TIMESTAMP}, MODIFIER = #{item.modifier,jdbcType=VARCHAR}, MODIFIEDTIME = #{item.modifiedtime,jdbcType=TIMESTAMP}, CREATOR_NAME = #{item.creator_name,jdbcType=VARCHAR}, MODIFIER_NAME = #{item.modifier_name,jdbcType=VARCHAR}
